import 'styles/test.css';
import React, { useState } from 'react';
import Header from 'components/Header'
import Sidebar from 'components/Sidebar';
import glazes from 'data/glazes.json'

function App() {
    const [selectedGlaze, setSelectedGlaze] = useState({id: "", base: "", recipe : []});
    console.log(glazes)

    return (<>
        <Header/>
        <section>
            <div className="main">
                <div className="glazes">
                    {glazes.map(glaze => 
                        <img 
                            src={`images/glazes/2x/${glaze.id}.png`} 
                            alt={`glaze with id: ${glaze.id}`}
                            onClick={() => {
                                setSelectedGlaze(glaze)
                            }}
                        />
                    )}
                </div>
            </div> 
            <Sidebar glaze={selectedGlaze}/>
        </section>
    </>)
}

export default App
