import 'styles/base.scss'
import React, { useState } from 'react'
import { HashRouter, Routes, Route, useLocation } from 'react-router-dom';
import About from 'components/about/About';
import Resources from 'components/resources/Resources';
import Glazes from 'components/glazes/Glazes'
import Base from 'Base';

function App() {
    const [showSidebar, setShowSidebar] = useState(false)
    const BLANK_GLAZE = {glaze: {id: "", base: "", additives : []}, base: {id: "", name: "", recipe : []}};
    const [selectedGlaze, setSelectedGlaze] = useState(BLANK_GLAZE)

    return (
        <HashRouter>
            <Routes>
                <Route path="/" element={<Base showSidebar={showSidebar}/>} >
                    <Route path="/" element={<p>TBD landing page</p>} />
                    <Route 
                        path="/glazes" 
                        element={<Glazes setShowSidebar={setShowSidebar} selectedGlaze={selectedGlaze} setSelectedGlase={selectedGlaze}/>} 
                    />
                    <Route path="/about" element={<About />} />
                    <Route path="/resources" element={<Resources />} />
                </Route>
            </Routes>
      </HashRouter>
    ) 
}

export default App
