import bases from 'data/bases.json'

function App() {
    const [selectedGlaze, setSelectedGlaze] = useState({glaze: {id: "", base: "", additives : []}, 
                                                        base: {id: "", name: "", recipe : []}});

    return (<>
        <Header/>
        <section>
            <Sidebar glaze={selectedGlaze}/>
            <div className="main">
                <div className="glazes">
                    {glazes.map(glaze => 
                        <img 
                            src={`images/glazes/2x/${glaze.id}.png`} 
                            alt={`glaze with id: ${glaze.id}`}
                            onClick={() => {
                                //setSelectedGlaze({glaze: glaze, base: bases.find(base => (base.id === glaze.id))})
                            }}
                        />
                    )}
                </div>
            </div> 
        </section>
    </>)
}

export default App
